import React, { useContext, useMemo } from "react";
import { Box, CardContent, Typography } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import VerticalSlider from "./VerticalSlider";
import { Card } from '@material-ui/core';
import BuildingCard from '../BuildingMenu/BuildingCard.js'




const useStyles = makeStyles(() => ({
    detailCard: {
        marginLeft: '10px',
        marginRight: '10px',
        marginTop: '10px',
        marginBottom: '10px',
        display: "inline-block"

    },
    cardContent: {
        display: "flex"
    }
}));

function DetailCard({ ol_uid }) {

    function getHourSliders() {
        let sliders = [];
        for (let i = 0; i <= 24; i++) {

            sliders.push(<React.Fragment key={i}> <VerticalSlider hour={i} /> </React.Fragment >)
        }
        return sliders

    }

    const classes = useStyles();

    return (
        <Card variant="outlined" className={classes.detailCard}>
            <div className={classes.cardContent}>
                <BuildingCard ol_uid={ol_uid} />
                <CardContent style={{ display: "flex" }}>
                    {getHourSliders()
                    }
                </CardContent>
            </div>


        </Card >);
}

export default DetailCard
