import React, { useEffect } from "react";
import "./Card.css"
import house from "../assets/house.svg"
import location from "../assets/location.svg"
import { Avatar } from '@material-ui/core';
import { Card } from '@material-ui/core';
import { CardContent } from '@material-ui/core';
import { CardHeader } from '@material-ui/core';
import { Typography } from '@material-ui/core/';
import { ToggleButtonGroup } from '@material-ui/lab';
import { ToggleButton } from '@material-ui/lab';


function BuildingCard() {

    const [buildingType, setBuildingType] = React.useState('Producer');

    const handleBuildingType = (event, newAlignment) => {
        if (newAlignment != null) {
            setBuildingType(newAlignment);
        }
    };

    return (
        <Card variant="outlined" style={{ display: "flex", justifyContent: "space-evenly" }}>
            <div style={{ flex: 1 }}>
                <CardHeader title="Edificio" avatar={<Avatar variant="square" className="card-avatar" src={house} />}> </CardHeader>

                <CardContent>
                    <ToggleButtonGroup
                        exclusive
                        value={buildingType}
                        onChange={handleBuildingType}
                        aria-label="building type"
                    >
                        <ToggleButton value="Producer" aria-label="left aligned" variant="text" >
                        </ToggleButton>
                        <ToggleButton value="Consumer" aria-label="centered">
                        </ToggleButton>
                        <ToggleButton value="Both" aria-label="right aligned">
                        </ToggleButton>
                    </ToggleButtonGroup>
                </CardContent >

            </div>

            <div style={{ display: "flex", "flex-direction": "column", flex: 3 }}>

                <CardHeader title="-3.92907, 38.98626" avatar={<Avatar variant="square" className="card-item" src={location} />}> </CardHeader>
                <CardHeader title="-3.92907, 38.98626" avatar={<Avatar variant="square" className="card-item" src={location} />}> </CardHeader>
                <Typography> Hola </Typography>

            </div>

        </Card >);
}

export default BuildingCard
