import 'fontsource-roboto';
import "ol/ol.css"

import React, { useState } from 'react';
import Map from "./Map";
import { Layers, TileLayer, VectorLayer } from "./Layers";
import { osm } from "./Source";
import { Controls, FullScreenControl, ZoomControl } from "./Controls";
import { Box } from "@material-ui/core"
import { BuildingContainer, } from './BuildingMenu';
import { AppBar } from '@material-ui/core';
import { fromLonLat } from 'ol/proj';
import { Fill, Stroke, Style } from 'ol/style';
import { makeStyles } from '@material-ui/core/styles';
import { Typography } from '@material-ui/core/';
import IconButton from '@material-ui/core/IconButton';
import MenuIcon from '@material-ui/icons/Menu';
import Toolbar from '@material-ui/core/Toolbar';



const useStyles = makeStyles((theme) => ({
    GridDesigner: {
        height: '100%',
        width: '100%',
    },
    Content: {
        height: '100%',
        width: '100%',
        overflow: "hidden",
        display: 'flex',
        marginTop: "1vw",
        marginBottom: "1vw"
    },
    menuButton: {
        marginRight: theme.spacing(2),
    },
    root: {
        flexGrow: 1,
    },
}));


let styles = {
    'Default': new Style({
        zIndex: 100,
        stroke: new Stroke({
            color: 'rgba(246, 207, 101, 1.0)',
            width: 1,
        }),
        fill: new Fill({
            color: 'rgba(255, 242, 175, 0.5)',
        }),
    }),
    'BuildingHighlight': new Style({
        fill: new Fill({
            color: 'rgba(255,255,255,0.7)',
        }),
        stroke: new Stroke({
            color: '#3399CC',
            width: 3,
        }),
    }),
};


const GridDesigner = () => {

    const [center, setCenter] = useState([-3.92907, 38.98626]);
    const [zoom, setZoom] = useState(18);
    const classes = useStyles();

    return (
        <Box className={classes.GridDesigner}>
            <div className={classes.root}>
                <AppBar position="static" style={{ backgroundColor: '#5f468a' }} >
                    <Toolbar>
                        <IconButton edge="start" className={classes.menuButton} color="inherit" aria-label="menu">
                            <MenuIcon />
                        </IconButton>
                        <Typography variant="h6" color="inherit" >
                            Grid designer
                        </Typography>
                    </Toolbar>
                </AppBar>
            </div >
            <Box className={classes.Content}>

                <Map center={fromLonLat(center)} zoom={zoom}>
                    <Layers>
                        <TileLayer
                            source={osm()}
                            zIndex={0}
                        />
                        <VectorLayer
                            defaultStyle={styles.Default} highlightStyle={styles.BuildingHighlight} renderZoom={17} centerSetter={setCenter}
                        />
                    </Layers>
                    <Controls>
                        <FullScreenControl />
                        <ZoomControl />
                    </Controls>
                </Map>
                <BuildingContainer />
            </Box>
        </Box >
    )
};

export default GridDesigner;
