import React, { useState, useEffect } from 'react';
import "ol/ol.css"
import './App.css';
import Map from "./Map";
import { Layers, TileLayer, VectorLayer } from "./Layers";
import { Fill, Stroke, Style } from 'ol/style';
import { osm } from "./Source";
import { Controls, FullScreenControl, ZoomControl } from "./Controls";
import { BuildingCard, BuildingContainer } from './BuildingMenu';
import 'fontsource-roboto';


let styles = {
	'Default': new Style({
		stroke: new Stroke({
			color: 'red',
			width: 1,
		}),
		fill: new Fill({
			color: 'rgba(255, 242, 175, 0.5)',
		}),
	}),
	'BuildingHighlight': new Style({
		fill: new Fill({
			color: 'rgba(255,255,255,0.7)',
		}),
		stroke: new Stroke({
			color: '#3399CC',
			width: 3,
		}),
	}),
};

const App = () => {

	const [center, setCenter] = useState([-3.92907, 38.98626]);
	const [zoom, setZoom] = useState(18);
	const [buildingList, setBuildiList] = useState([]);

	const handleSelectBuilding = (building) => {
		console.log(building);

	}

	return (
		<div className="app">
			<Map center={center} zoom={zoom} onClickHandler={handleSelectBuilding}>
				<Layers>
					<TileLayer
						source={osm()}
						zIndex={0}
					/>
					<VectorLayer
						style={styles.Default}
					/>
				</Layers>
				<Controls>
					<FullScreenControl />
					<ZoomControl />
				</Controls>
			</Map>
			<BuildingContainer>
				<BuildingCard latitude="38.98626" longitude="-3.92907" area="200 m2" />
				<BuildingCard latitude="38.98626" longitude="-3.92907" area="200 m2" />
			</BuildingContainer>
		</div>
	)
};

export default App;
