import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router } from 'react-router-dom'
import "ol/ol.css"
import './App.css';
import Map from "./Map";
import { Layers, TileLayer, VectorLayer } from "./Layers";
import { osm } from "./Source";
import { Controls, FullScreenControl, ZoomControl } from "./Controls";
import { BuildingContainer, } from './BuildingMenu';
import 'fontsource-roboto';
import { fromLonLat } from 'ol/proj';
import { Fill, Stroke, Style } from 'ol/style';


import { makeStyles } from '@material-ui/core/styles';

const useStyles = makeStyles(() => ({
	'#root': {
		margin: 0,
		height: '98vh',
		width: '98vw'
	},

	app: {
		height: '100%',
		width: '100%',
		marginLeft: '30px',
		marginRight: '30px',
		marginTop: '10px',
		marginBottom: '10px',
		display: 'flex',
	}

}));


let styles = {
	'Default': new Style({
		zIndex: 100,
		stroke: new Stroke({
			color: 'rgba(246, 207, 101, 1.0)',
			width: 1,
		}),
		fill: new Fill({
			color: 'rgba(255, 242, 175, 0.5)',
		}),
	}),
	'BuildingHighlight': new Style({
		fill: new Fill({
			color: 'rgba(255,255,255,0.7)',
		}),
		stroke: new Stroke({
			color: '#3399CC',
			width: 3,
		}),
	}),
};


const App = () => {

	const [center, setCenter] = useState([-3.92907, 38.98626]);
	const [zoom, setZoom] = useState(18);

	return (
		<div className="app">
			<Map center={fromLonLat(center)} zoom={zoom}>
				<Layers>
					<TileLayer
						source={osm()}
						zIndex={0}
					/>
					<VectorLayer
						defaultStyle={styles.Default} highlightStyle={styles.BuildingHighlight} renderZoom={17} centerSetter={setCenter}
					/>
				</Layers>
				<Controls>
					<FullScreenControl />
					<ZoomControl />
				</Controls>
			</Map>
			<BuildingContainer />
		</div>
	)
};

export default App;
