import React, { useContext } from "react";
import { makeStyles } from '@material-ui/core/styles';
import { Box, Hidden } from "@material-ui/core"
import { Typography } from '@material-ui/core/';
import BuildingCard from './BuildingCard'
import { useSelector, useDispatch } from 'react-redux'
import { Scrollbars } from 'react-custom-scrollbars';
import Button from '@material-ui/core/Button';
import { useHistory } from 'react-router-dom';
import {
    Link
} from "react-router-dom";


const useStyles = makeStyles(() => ({
    buildingContainer: {
        flex: 2,
        marginLeft: '1vw',
        marginRight: '1vw',
        display: 'flex',
        flexDirection: "column"
    },
    buildingList: {
        flex: 10
    },
    buildingDetailButton: {
        flex: 1,
        height: "100%",
        width: "100%"
    }
}));

const renderThumb = ({ style, ...props }) => {
    const thumbStyle = {
        borderRadius: 6,
        backgroundColor: 'rgba(35, 49, 86, 0.8)'
    };
    return <div style={{ ...style, ...thumbStyle }} {...props} />;
};

const CustomScrollbars = props => (
    <Scrollbars
        renderThumbHorizontal={renderThumb}
        renderThumbVertical={renderThumb}
        {...props}
    />
);

function BuildingContainer() {

    const classes = useStyles();
    const { push } = useHistory();
    const buildingList = useSelector(state => state.buildings);

    return (
        <Box className={classes.buildingContainer}>
            <Box className={classes.buildingList}>
                <CustomScrollbars autoHide autoHideTimeout={500} autoHideDuration={200}>
                    <Typography variant="h5" align="center"> Edificios seleccionados</Typography>
                    {
                        Object.keys(buildingList).map((dictkey, index) => (
                            <React.Fragment key={dictkey}>
                                <BuildingCard ol_uid={dictkey} />
                            </React.Fragment>
                        ))
                    }
                </CustomScrollbars>
            </Box>
            <Box className={classes.buildingDetailButton}>
                <Button color="secondary" component={Link} to="/detail">
                    Smart grid detail
                </Button>
            </Box>

        </Box >

    )

}

export default BuildingContainer
