import React, { useState, useEffect } from 'react';
import "ol/ol.css"
import './App.css';
import Map from "./Map";
import { Layers, TileLayer, VectorLayer } from "./Layers";
import { osm } from "./Source";
import { Controls, FullScreenControl, ZoomControl } from "./Controls";
import { BuildingContainer, BuildingListContext } from './BuildingMenu';
import 'fontsource-roboto';
import { fromLonLat } from 'ol/proj';




const App = () => {

	const [center, setCenter] = useState([-3.92907, 38.98626]);
	const [zoom, setZoom] = useState(18);
	//{'1234': { latitude: "38.98626", longitude: "-3.92907", area: "200 m2" }}
	const [buildingList, setBuildingList] = useState({ '1234': { latitude: "38.98626", longitude: "-3.92907", area: "200 m2" } });

	return (
		<BuildingListContext.Provider value={[buildingList, setBuildingList]}>
			<div className="app">
				<Map center={fromLonLat(center)} zoom={zoom}>
					<Layers>
						<TileLayer
							source={osm()}
							zIndex={0}
						/>
						<VectorLayer
						/>
					</Layers>
					<Controls>
						<FullScreenControl />
						<ZoomControl />
					</Controls>
				</Map>
				<BuildingContainer>
				</BuildingContainer>
			</div>
		</BuildingListContext.Provider >
	)
};

export default App;
