import React, { useContext } from "react";
import { CardContent } from '@material-ui/core';
import { CardHeader } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import { useSelector, useDispatch } from 'react-redux'
import { updateBuildingType } from '../redux/actions/buildingActions.js'
import VerticalSlider from "./VerticalSlider";
import { Card } from '@material-ui/core';




const useStyles = makeStyles(() => ({
    sizeBuilding: {
        height: 50,
        width: 50,
    },
    sizeAvatar: {
        height: 30,
        width: 30,
    },
}));

function DetailCard({ ol_uid }) {

    function getHourSliders() {
        let sliders = [];
        for (let i = 0; i <= 24; i++) {
            sliders.push(<VerticalSlider hour={i} />)
        }
        return sliders
    }

    const classes = useStyles();
    const building = useSelector(state => state.buildings[ol_uid]);
    const dispatch = useDispatch();

    return (
        <Card variant="outlined" style={{ marginLeft: '10px', marginRight: '10px', marginTop: '10px', marginBottom: '10px', display: "inline-block" }}>
            <CardContent style={{ display: "flex" }}>
                {getHourSliders()}
            </CardContent>


        </Card >);
}

export default DetailCard
