    },
}));

function DetailCard({ ol_uid }) {

    function getHourSliders() {
        let sliders = [];
        for (let i = 0; i <= 24; i++) {
            sliders.push(<VerticalSlider key={i} hour={i} />)
        }
        return sliders
    }


    const sliders = useMemo(() => getHourSliders(), [])
    const classes = useStyles();

    return (
        <Card variant="outlined" style={{ marginLeft: '10px', marginRight: '10px', marginTop: '10px', marginBottom: '10px', display: "inline-block" }}>
            <Typography variant="h6">  </Typography>
            <CardContent style={{ display: "flex" }}>
                {sliders}
            </CardContent>


        </Card >);
}

export default DetailCard
