import React, { useEffect } from "react";
import house from "../assets/house.svg"
import location from "../assets/location.svg"
import area from "../assets/area.svg"
import { Avatar } from '@material-ui/core';
import { Card } from '@material-ui/core';
import { CardContent } from '@material-ui/core';
import { CardHeader } from '@material-ui/core';
import { Typography } from '@material-ui/core/';
import { makeStyles } from '@material-ui/core/styles';
import { ToggleButtonGroup } from '@material-ui/lab';
import { ToggleButton } from '@material-ui/lab';


const useStyles = makeStyles(() => ({
    sizeAvatar: {
        height: 30,
        width: 30,
        marginLeft: 10,
        marginRight: 5
    },
}));

function BuildingCard(location, area) {

    const [buildingType, setBuildingType] = React.useState('Producer');
    const classes = useStyles();

    const handleBuildingType = (event, newAlignment) => {
        if (newAlignment != null) {
            setBuildingType(newAlignment);
        }
    };

    return (
        <Card variant="outlined" style={{ display: "flex" }}>
            <div style={{ flex: 1 }}>
                <CardHeader title="Edificio" avatar={<Avatar variant="square" className="card-avatar" src={house} />}> </CardHeader>

                <CardContent>
                    <ToggleButtonGroup
                        exclusive
                        value={buildingType}
                        onChange={handleBuildingType}
                        aria-label="building type"
                    >
                        <ToggleButton value="Producer" aria-label="left aligned" variant="text" >
                        </ToggleButton>
                        <ToggleButton value="Consumer" aria-label="centered">
                        </ToggleButton>
                        <ToggleButton value="Both" aria-label="right aligned">
                        </ToggleButton>
                    </ToggleButtonGroup>
                </CardContent >

            </div>

            <div style={{ display: "flex", "flexDirection": "column", flex: 3, justifyContent: 'space-evenly' }}>
                <div style={{ display: "flex", "flexDirection": "row", margin: "5px" }}>
                    <Avatar className={classes.sizeAvatar} variant="square" src={location} />
                    <Typography> {{ location }} </Typography>
                </div>
                <div style={{ display: "flex", "flexDirection": "row", margin: "5px" }}>
                    <Avatar className={classes.sizeAvatar} variant="square" src={area} />
                    <Typography> {{ area }} </Typography>
                </div>
            </div>

        </Card >);
}

export default BuildingCard
