import { ADD_BUILDING, CLICK_BUILDING, REMOVE_BUILDING, UPDATE_BUILDING_TYPE } from '../actions/types'

const initialState = { '1234': { latitude: "38.98626", longitude: "-3.92907", area: "200 m2", type: 'Both' } };

const reducer = (state = initialState, action) => {
    switch (action.type) {
        case UPDATE_BUILDING_TYPE: {
            let stateCloned = Object.assign({}, state);
            stateCloned[action.building_id].type = action.building_type;
            return stateCloned;
        }
        case ADD_BUILDING:
            {
                let stateCloned = Object.assign({}, state);
                stateCloned[action.building_data.id] = {
                    latitude: action.building_data.latitude,
                    longitude: action.building_data.longitude,
                    area: action.building_data.area,
                    type: 'Both'
                }
                return stateCloned;
            }
        case REMOVE_BUILDING: {
            let stateCloned = Object.assign({}, state);
            delete stateCloned[action.building_id]
            return stateCloned;
        }
        case CLICK_BUILDING: {
            let stateCloned = Object.assign({}, state);

            delete stateCloned[action.building_id]
            return stateCloned;
        }
        default:
            return state;
    }

};


export default reducer;
